package DAY2;

public class BreakDemo {
	
    // This is the correct main method for the JVM to execute.
    public static void main(String[] args) {
        // TODO Auto-generated method stub
    	for (int i = 5; i < 10; i++) {
    		
    	  if (i == 5)
    	        break; // This immediately exits the loop on the very first iteration (i=5).
    	  
    	   System.out.println(i);
    	}
    }
}  
    	
      